import React, { useState, useEffect } from 'react';
import { Button, Modal } from 'react-bootstrap';

function AwaitingTableTest() {
    const [awaitingData, setAwaitingData] = useState(null);
    const [selectedEpic, setSelectedEpic] = useState(null);
    const [modalOpen, setModalOpen] = useState(false);

    useEffect(() => {
        const fetchAwaiting = async () => {
            try {
                const response = await fetch(`http://localhost:4000/api/awaiting-data`);
                if (!response.ok) {
                    throw new Error('Network response for awaiting data was not ok');
                }
                const data = await response.json();
                setAwaitingData(data);
            } catch (error) {
                console.error('Error fetching awaiting data:', error);
            }
        };

        fetchAwaiting();
    }, []);

    // Group issues by epic name
    const groupedByEpic = {};
    if (awaitingData) {
        awaitingData.forEach(issue => {
            const epicName = issue.epicName;
            if (!groupedByEpic[epicName]) {
                groupedByEpic[epicName] = [];
            }
            groupedByEpic[epicName].push(issue);
        });
    }

    const handleEpicClick = epicName => {
        setSelectedEpic(epicName);
        setModalOpen(true);
    };

    const handleCloseModal = () => {
        setSelectedEpic(null);
        setModalOpen(false);
    };

    return (
        <div>
            <h2>Awaiting Approval Issues</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Epic Name</th>
                    </tr>
                </thead>
                <tbody>
                    {Object.keys(groupedByEpic).map(epicName => (
                        <tr key={epicName} onClick={() => handleEpicClick(epicName)}>
                            <td>{epicName}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Bootstrap modal */}
            <Modal show={modalOpen} onHide={handleCloseModal}>
                <Modal.Header closeButton>
                    <Modal.Title>Issues for Epic: {selectedEpic}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <ul>
                        {groupedByEpic[selectedEpic]?.map(issue => (
                            <li key={issue.key}>{issue.key} - {issue.storyPoints}</li>
                        ))}
                    </ul>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleCloseModal}>Close</Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}

export default AwaitingTableTest;
