import React, { useState, useEffect } from "react";
import { Button, Modal } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

function MoodCheck() {
  const [showModal, setShowModal] = useState(false);
  const [availableTotal, setAvailableTotal] = useState(false);

  useEffect(() => {
    const fetchAvailableTotal = async () => {
      try {
        const response = await fetch(`http://localhost:4000/api/check-mood`);
        if (!response.ok) {
          throw new Error('Network response for available data was not ok');
        }
        const data = await response.json();
        setAvailableTotal(data);
      } catch (error) {
        console.error('Error fetching available total data:', error);
      }
    };

    fetchAvailableTotal();
  }, []);

  const handleShowModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div>
        <h2 style={{ textAlign: 'left' }}>How's My Mood?</h2>
        <h5 style={{ textAlign: 'left', fontSize: '15px', marginBottom: '20px' }}>Send reports to your colleagues</h5>
        </div>
        <div style={{ fontSize: '20px', marginBottom: '20px', color: '#1479FF' }}>
        <img src="/moodcheck.png" className="img-fluid" alt="Responsive image" />
        </div>
      </div>
      <div>
        <Button onClick={handleShowModal} style={{ width: '100%', backgroundColor: 'red' }}>Check Mood</Button>
      </div>
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton style={{ backgroundColor: "#0F415A", color: "white" }}>
          <Modal.Title>How's Josh feeling today?</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {availableTotal < 300 ? (
            <img src="/moodcheck1.png" className="img-fluid" alt="Responsive image" />
          ) : (
            <img src="/moodcheck2.png" className="img-fluid" alt="Responsive image" />
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal} style={{ backgroundColor: 'red' }}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default MoodCheck;
