import React, { useState, useEffect } from 'react';
import { Chart } from "react-google-charts";

function App() {
  const [sprData, setSprData] = useState([]);
  const [blockedSprData, setBlockedSprData] = useState([]);
  const [chartKey, setChartKey] = useState(0);

  const fetchData = async () => {
    try {
      const responseSpr = await fetch('http://localhost:4000/jira/spr');
      const responseDataSpr = await responseSpr.json();
      setSprData(responseDataSpr.issues);

      const responseBlockedSpr = await fetch('http://localhost:4000/jira/blocked-spr');
      const responseDataBlockedSpr = await responseBlockedSpr.json();
      setBlockedSprData(responseDataBlockedSpr.issues);

      setChartKey(prevKey => prevKey + 1);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const calculatePercentage = (data, total) => {
    return Math.round((data.length / total) * 100);
  };

  const getTotalItemCount = () => {
    return sprData.length + blockedSprData.length;
  };

  const getChartData = () => {
    const totalItems = getTotalItemCount();
    const sprPercentage = calculatePercentage(sprData, totalItems);
    const blockedSprPercentage = calculatePercentage(blockedSprData, totalItems);

    return [
      ["Task", "Percentage"],
      ["SPR Data", sprPercentage],
      ["Blocked SPR Data", blockedSprPercentage]
    ];
  };

  const options = {
    title: "SPR Data",
  };

  return (
    <div>
      <h1>JIRA Data</h1>
      <div>
        <h2>Spr Data vs Blocked SPR Data</h2>
        <Chart
          chartType="PieChart"
          data={getChartData()}
          options={options}
          width={"100%"}
          height={"400px"}
        />
      </div>
    </div>
  );
}

export default App;
