import React, { useState, useEffect } from 'react';
import { Button } from 'react-bootstrap';

function AwaitingTableTest() {
    const [awaitingData, setAwaitingData] = useState(null);
    const [selectedEpic, setSelectedEpic] = useState(null);
    const [modalOpen, setModalOpen] = useState(false);

    useEffect(() => {
        const fetchAwaiting = async () => {
            try {
                const response = await fetch(`http://localhost:4000/api/awaiting-data`);
                if (!response.ok) {
                    throw new Error('Network response for awaiting data was not ok');
                }
                const data = await response.json();
                setAwaitingData(data);
            } catch (error) {
                console.error('Error fetching awaiting data:', error);
            }
        };

        fetchAwaiting();
    }, []);

    // Group issues by epic name
    const groupedByEpic = {};
    if (awaitingData) {
        awaitingData.forEach(issue => {
            const epicName = issue.epicName;
            if (!groupedByEpic[epicName]) {
                groupedByEpic[epicName] = [];
            }
            groupedByEpic[epicName].push(issue);
        });
    }

    const handleEpicClick = epicName => {
        setSelectedEpic(epicName);
        setModalOpen(true);
    };

    const handleCloseModal = () => {
        setModalOpen(false);
    };

    return (
        <div>
            <h2>Awaiting Approval Issues</h2>
            <table className="table">
                <thead>
                    <tr>
                        <th>Epic Name</th>
                    </tr>
                </thead>
                <tbody>
                    {Object.keys(groupedByEpic).map(epicName => (
                        <tr key={epicName} onClick={() => handleEpicClick(epicName)}>
                            <td>{epicName}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Bootstrap modal */}
            <div className={`modal fade ${modalOpen ? 'show' : ''}`} style={{ display: modalOpen ? 'block' : 'none' }}>
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title">Issues for Epic: {selectedEpic}</h5>
                            <button type="button" className="btn-close" aria-label="Close" onClick={handleCloseModal}></button>
                        </div>
                        <div className="modal-body">
                            <ul>
                                {groupedByEpic[selectedEpic]?.map(issue => (
                                    <li key={issue.key}>{issue.key} - {issue.storyPoints}</li>
                                ))}
                            </ul>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" onClick={handleCloseModal}>Close</button>
                        </div>
                    </div>
                </div>
            </div>
            {modalOpen && <div className="modal-backdrop fade show" onClick={handleCloseModal}></div>}
        </div>
    );
}

export default AwaitingTableTest;
