import React, { useState, useEffect } from 'react';
import { Chart } from "react-google-charts";



function App() {
  const [sprData, setSprData] = useState([]);
  const [blockedSprData, setBlockedSprData] = useState([]);
  const [chartKey, setChartKey] = useState(0);

  const fetchData = async () => {
    try {
      const responseSpr = await fetch('http://localhost:4000/jira/spr');
      const responseDataSpr = await responseSpr.json();
      setSprData(responseDataSpr.issues);

      const responseBlockedSpr = await fetch('http://localhost:4000/jira/blocked-spr');
      const responseDataBlockedSpr = await responseBlockedSpr.json();
      setBlockedSprData(responseDataBlockedSpr.issues);

      setChartKey(prevKey => prevKey + 1);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const data = [
    ["Task", "Hours per Day"],
    ["Work", 11],
    ["Eat", 2],
    ["Commute", 2],
    ["Watch TV", 2],
    ["Sleep", 7],
  ];

  const options = {
    title: "SPR Data",
  };

  console.log(sprData)


  return (
    <div>
      <h1>JIRA Data</h1>
      <div>
        <h2>Spr Data</h2>
        <Chart
            chartType="PieChart"
            data={data}
            options={options}
            width={"100%"}
            height={"400px"}
          />
      </div>
    </div>
  );
}


export default App;
