import React, { useState, useEffect } from "react";
import { Button, Modal } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

function MoodCheck() {
  const [showModal, setShowModal] = useState(false);
  const [availableTotal, setAvailableTotal] = useState(false);

  useEffect(() => {
    const fetchAvailableTotal = async () => {
        try {
            const response = await fetch(`http://localhost:4000/api/available-total`);
            if (!response.ok) {
                throw new Error('Network response for available data was not ok');
            }
            const data = await response.json();
            setAvailableTotal(data);
        } catch (error) {
            console.error('Error fetching available total data:', error);
            setError(error);
        }
    };

    fetchAvailableTotal();
  }, []);

  console.log(availableTotal)

  const handleShowModal = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  return (
    <div>
      <div>
        <h2 style={{ textAlign: 'left' }}>Hows My Mood?</h2>
      </div>
      <div>
        <Button onClick={handleShowModal}>Check Mood</Button>
      </div>
      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Mood Check</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>How are you feeling today?</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default MoodCheck;
