import React, { useState, useEffect } from 'react';
import PieChart from './PieChart';

function App() {
  const [sprData, setSprData] = useState([]);
  const [blockedSprData, setBlockedSprData] = useState([]);

  const fetchSpr = async () => {
    try {
      const response = await fetch('http://localhost:4000/jira/spr');
      const responseData = await response.json();
      setSprData(responseData.issues); 
    } catch (error) {
      console.error('Error fetching SPR data:', error);
    }
  };

  const fetchBlockedSpr = async () => {
    try {
      const response = await fetch('http://localhost:4000/jira/blocked-spr');
      const responseData = await response.json();
      setBlockedSprData(responseData.issues); 
    } catch (error) {
      console.error('Error fetching Blocked SPR data:', error);
    }
  };

  useEffect(() => {
    fetchSpr();
    fetchBlockedSpr();
  }, []);

  return (
    <div>
      <h1>JIRA Data</h1>
      <div style={{ display: 'flex' }}>
        <div style={{ flex: 1 }}>
          <h2>SPR Data</h2>
          <PieChart data={sprData} />
        </div>
        <div style={{ flex: 1 }}>
          <h2>Blocked SPR Data</h2>
          <PieChart data={blockedSprData} />
        </div>
      </div>
    </div>
  );
}

export default App;
