import React, { useState, useEffect } from "react";
import { Button, Modal } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

function AwaitingTable() {
  const [awaitingData, setAwaitingData] = useState(null);
  const [selectedEpic, setSelectedEpic] = useState(null);
  const [modalOpen, setModalOpen] = useState(false);
  const [epicTotals, setEpicTotals] = useState({});
  const [parentIssueLink, setParentIssueLink] = useState(null);

  useEffect(() => {
    const fetchAwaiting = async () => {
      try {
        const response = await fetch(`http://localhost:4000/api/awaiting-table`);
        if (!response.ok) {
          throw new Error("Network response for awaiting data was not ok");
        }
        const data = await response.json();
        setAwaitingData(data);
      } catch (error) {
        console.error("Error fetching awaiting data:", error);
      }
    };

    fetchAwaiting();
  }, []);

  useEffect(() => {
    if (awaitingData) {
      const totals = {};
      awaitingData.forEach((issue) => {
        const epicName = issue.epicName;
        if (!totals[epicName]) {
          totals[epicName] = 0;
        }
        totals[epicName] += issue.storyPoints || 0;
      });
      setEpicTotals(totals);
    }
  }, [awaitingData]);

  const handleEpicClick = (epicName) => {
    setSelectedEpic(epicName);
    setModalOpen(true);

    const selectedEpicData = awaitingData.find(
      (issue) => issue.epicName === epicName,
    );
    if (selectedEpicData) {
      setParentIssueLink(selectedEpicData.parentIssueLink); 
    } else {
      setParentIssueLink(null);
    }
  };

  const handleCloseModal = () => {
    setSelectedEpic(null);
    setModalOpen(false);
    setParentIssueLink(null); 
  };

  return (
    <div>

<div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <div>
        <h2 style={{ textAlign: 'left' }}>Pending Client Approval</h2>
        <h5 style={{ textAlign: 'left', fontSize: '15px', marginBottom: '20px' }}>The client will need to approve these</h5>
        </div>
        <div>
        <img src="/waitingimage.png" className="img-fluid" alt="Responsive image" style={{ width: '100%', height: '45px' }}/>
        </div>
      </div>
      <table className="table table-hover table-bordered">
        <thead>
          <tr>
            <th
              scope="col"
              style={{ backgroundColor: "#0F415A", color: "white" }}
            >
              Epic Name
            </th>
            <th
              scope="col"
              style={{ backgroundColor: "#0F415A", color: "white" }}
            >
              Total Story Points
            </th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(epicTotals).map((epicName) => (
            <tr key={epicName} onClick={() => handleEpicClick(epicName)}>
              <td>{epicName}</td>
              <td>{epicTotals[epicName]}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <Modal show={modalOpen} onHide={handleCloseModal}>
        <Modal.Header style={{ backgroundColor: "#0F415A", color: "white" }}>
          <Modal.Title>Issues: {selectedEpic}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <table className="table">
            <thead>
              <tr>
                <th>MSS ID</th>
                <th>Story Points</th>
              </tr>
            </thead>
            <tbody>
              {awaitingData &&
                awaitingData
                  .filter((issue) => issue.epicName === selectedEpic)
                  .map((issue) => (
                    <tr key={issue.key}>
                      <td>
                        <a
                          href={`https://redantinternal.atlassian.net/browse/${issue.key}`}
                          target="_blank"
                        >
                          {issue.key}
                        </a>
                      </td>
                      <td>{issue.storyPoints}</td>
                    </tr>
                  ))}
            </tbody>
          </table>
        </Modal.Body>

        {parentIssueLink && (
          <Modal.Footer
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <div style={{ fontWeight: "bold", color: "black" }}>
              <span>Epic Link: </span>
              <a
                href={`https://redantinternal.atlassian.net/browse/${selectedEpic}`}
                target="_blank"
                style={{
                  textDecoration: "none",
                  color: "blue",
                  borderBottom: "1px solid",
                }}
              >
                {selectedEpic}
              </a>
            </div>
            <Button
              variant="secondary"
              onClick={handleCloseModal}
              style={{ backgroundColor: "red" }}
            >
              Close
            </Button>
          </Modal.Footer>
        )}
      </Modal>
    </div>
  );
}

export default AwaitingTable;
