import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

function SendReport() {
  const [email, setEmail] = useState("");

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handleSendReport = () => {
    setEmail("");
  };

  return (
    <div>
      <div>
        <h1>Send A Full Report <p>To Your Email</p></h1>
      </div>
      <div>
        <Form>
          <Form.Group controlId="email" style={{ display: 'flex' }}>
            <Form.Control
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={handleEmailChange}
              style={{ flex: 1, marginRight: '10px' }} // Adjust width
            />
            <Button onClick={handleSendReport} style={{ backgroundColor: '#0F415A', width: '150px' }}>Send Report</Button>
          </Form.Group>
        </Form>
      </div>
    </div>
  );
}

export default SendReport;
