import React, { useState, useEffect } from 'react';
import { Button, Modal } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function AwaitingTableTest() {
    const [awaitingData, setAwaitingData] = useState(null);
    const [selectedEpic, setSelectedEpic] = useState(null);
    const [modalOpen, setModalOpen] = useState(false);
    const [epicTotals, setEpicTotals] = useState({}); // State to store total story points for each epic

    useEffect(() => {
        const fetchAwaiting = async () => {
            try {
                const response = await fetch(`http://localhost:4000/api/awaiting-data`);
                if (!response.ok) {
                    throw new Error('Network response for awaiting data was not ok');
                }
                const data = await response.json();
                setAwaitingData(data);
            } catch (error) {
                console.error('Error fetching awaiting data:', error);
            }
        };

        fetchAwaiting();
    }, []);

    // Group issues by epic name and calculate total story points for each epic
    useEffect(() => {
        if (awaitingData) {
            const totals = {};
            awaitingData.forEach(issue => {
                const epicName = issue.epicName;
                if (!totals[epicName]) {
                    totals[epicName] = 0;
                }
                totals[epicName] += issue.storyPoints || 0;
            });
            setEpicTotals(totals);
        }
    }, [awaitingData]);

    const handleEpicClick = epicName => {
        setSelectedEpic(epicName);
        setModalOpen(true);
    };

    const handleCloseModal = () => {
        setSelectedEpic(null);
        setModalOpen(false);
    };

    return (
        <div style={{ width: '40%', marginLeft: '10px' }}>
            <h2>Awaiting Approval Issues</h2>

            <table className="table table-hover table-bordered">
                <thead>
                    <tr>
                        <th scope="col" style={{ backgroundColor: '#0F415A', color: 'white' }}>Epic Name</th>
                        <th scope="col" style={{ backgroundColor: '#0F415A', color: 'white' }}>Total Story Points</th>
                    </tr>
                </thead>
                <tbody>
                    {Object.keys(epicTotals).map(epicName => (
                        <tr key={epicName} onClick={() => handleEpicClick(epicName)}>
                            <td>{epicName}</td>
                            <td>{epicTotals[epicName]}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <Modal show={modalOpen} onHide={handleCloseModal}>
                <Modal.Header>
                    <Modal.Title>Issues for Epic: {selectedEpic}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <ul>
                    {awaitingData && awaitingData
    .filter(issue => issue.epicName === selectedEpic)
    .map(issue => (
        <li key={issue.key}>{issue.key} - {issue.storyPoints}</li>
    ))}

                    </ul>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleCloseModal}>Close</Button>
                </Modal.Footer>
            </Modal>
        </div>
    );
}

export default AwaitingTableTest;
